package esan.mendoza.impulso.data.local.entities

import androidx.room.ColumnInfo
import androidx.room.Entity
import androidx.room.PrimaryKey

@Entity(tableName = "category")
data class Category(
    @PrimaryKey(autoGenerate = true)
    val id: Int = 0, // Valor por defecto para permitir autoGenerate

    @ColumnInfo(name = "Nombre")
    val nombre: String,

    @ColumnInfo(name = "icono")
    val icono: String,

    @ColumnInfo(name = "created_at")
    val createdAt: String,

    @ColumnInfo(name = "updated_at")
    val updatedAt: String
)

package esan.mendoza.impulso.data.local.entities

import androidx.room.ColumnInfo
import androidx.room.Entity
import androidx.room.ForeignKey
import androidx.room.Index
import androidx.room.PrimaryKey

@Entity(
    tableName = "recurso",
    foreignKeys = [
        ForeignKey(
            entity = Category::class,
            parentColumns = ["id"],
            childColumns = ["categoria_id"],
            onDelete = ForeignKey.CASCADE
        )
    ],
    indices = [Index(value = ["categoria_id"])]
)
data class Recurso(
    @PrimaryKey(autoGenerate = true)
    val id: Int,

    @ColumnInfo(name = "Nombre")
    val nombre: String,

    @ColumnInfo(name = "Descripcion")
    val descripcion: String,

    @ColumnInfo(name = "categoria_id")
    val categoriaId: Int,

    @ColumnInfo(name = "Link")
    val link: String,

    @ColumnInfo(name = "created_at")
    val createdAt: String, // Puedes usar Long para timestamp o Date

    @ColumnInfo(name = "is_favorite")
    val isFavorite: Boolean = false
)

package esan.mendoza.impulso.data.local.entities

import androidx.room.ColumnInfo
import androidx.room.Entity
import androidx.room.ForeignKey
import androidx.room.Index
import androidx.room.PrimaryKey

@Entity(
    tableName = "recurso",
    foreignKeys = [
        ForeignKey(
            entity = Category::class,
            parentColumns = ["id"],
            childColumns = ["categoria_id"],
            onDelete = ForeignKey.CASCADE
        )
    ],
    indices = [Index(value = ["categoria_id"])]
)
data class Recurso(
    @PrimaryKey(autoGenerate = true)
    val id: Int = 0, // Valor por defecto para permitir autoGenerate

    @ColumnInfo(name = "Nombre")
    val nombre: String,

    @ColumnInfo(name = "Descripcion")
    val descripcion: String,

    @ColumnInfo(name = "categoria_id")
    val categoriaId: Int,

    @ColumnInfo(name = "Link")
    val link: String,

    @ColumnInfo(name = "created_at")
    val createdAt: String, // Puedes usar Long para timestamp o Date

    @ColumnInfo(name = "updated_at")
    val updatedAt: String, // Puedes usar Long para timestamp o Date

    @ColumnInfo(name = "is_favorite")
    val isFavorite: Boolean = false
)

package esan.mendoza.impulso.di

import android.content.Context
import androidx.room.Room
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.android.qualifiers.ApplicationContext
import dagger.hilt.components.SingletonComponent
import esan.mendoza.impulso.data.db.AppDatabase
import esan.mendoza.impulso.data.local.dao.CategoryDao
import esan.mendoza.impulso.data.local.dao.RecursoDao
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
object DatabaseModule {

    @Provides
    @Singleton
    fun provideAppDatabase(@ApplicationContext context: Context): AppDatabase {
        return Room.databaseBuilder(
            context.applicationContext,
            AppDatabase::class.java,
            "app_database"
        ).build()
    }

    @Provides
    fun provideCategoryDao(database: AppDatabase): CategoryDao {
        return database.categoryDao()
    }

    @Provides
    fun provideRecursoDao(database: AppDatabase): RecursoDao {
        return database.recursoDao()
    }
}

package esan.mendoza.impulso.di

import android.content.Context
import androidx.room.Room
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.android.qualifiers.ApplicationContext
import dagger.hilt.components.SingletonComponent
import esan.mendoza.impulso.data.db.AppDatabase
import esan.mendoza.impulso.data.local.dao.CategoryDao
import esan.mendoza.impulso.data.local.dao.RecursoDao
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
object DatabaseModule {

    @Provides
    @Singleton
    fun provideAppDatabase(@ApplicationContext context: Context): AppDatabase {
        return Room.databaseBuilder(
            context.applicationContext,
            AppDatabase::class.java,
            "app_database"
        )
        .fallbackToDestructiveMigration() // Permite recrear la DB si hay problemas de migración
        .build()
    }

    @Provides
    fun provideCategoryDao(database: AppDatabase): CategoryDao {
        return database.categoryDao()
    }

    @Provides
    fun provideRecursoDao(database: AppDatabase): RecursoDao {
        return database.recursoDao()
    }
}

package esan.mendoza.impulso.presentation.component

import androidx.compose.foundation.background
import androidx.compose.foundation.border
import androidx.compose.foundation.clickable
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.grid.GridCells
import androidx.compose.foundation.lazy.grid.LazyVerticalGrid
import androidx.compose.foundation.lazy.grid.items
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import esan.mendoza.impulso.presentation.component.IconPicker

@Composable
fun DialogAddCategory(
    show: Boolean,
    onDismiss: () -> Unit,
    onAccept: (String, String) -> Unit
) {
    var nombre by remember { mutableStateOf("") }
    var selectedIcon by remember { mutableStateOf<IconPicker.IconOption?>(null) }
    var showIconPicker by remember { mutableStateOf(false) }

    if (show) {
        AlertDialog(
            onDismissRequest = onDismiss,
            title = { Text("Agregar Categoría", fontWeight = FontWeight.Bold) },
            text = {
                Column {
                    OutlinedTextField(
                        value = nombre,
                        onValueChange = { nombre = it },
                        label = { Text("Nombre de la categoría") },
                        modifier = Modifier.fillMaxWidth(),
                        singleLine = true
                    )

                    Spacer(modifier = Modifier.height(16.dp))

                    Text(
                        text = "Seleccionar ícono",
                        style = MaterialTheme.typography.bodyMedium,
                        fontWeight = FontWeight.Medium
                    )

                    Spacer(modifier = Modifier.height(8.dp))

                    // Botón para seleccionar ícono
                    Card(
                        modifier = Modifier
                            .fillMaxWidth()
                            .clickable { showIconPicker = !showIconPicker },
                        elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
                    ) {
                        Row(
                            modifier = Modifier
                                .fillMaxWidth()
                                .padding(16.dp),
                            horizontalArrangement = Arrangement.spacedBy(12.dp),
                            verticalAlignment = Alignment.CenterVertically
                        ) {
                            if (selectedIcon != null) {
                                Icon(
                                    imageVector = selectedIcon!!.icon,
                                    contentDescription = null,
                                    modifier = Modifier.size(24.dp),
                                    tint = MaterialTheme.colorScheme.primary
                                )
                                Text(
                                    text = selectedIcon!!.name,
                                    style = MaterialTheme.typography.bodyMedium
                                )
                            } else {
                                Icon(
                                    imageVector = IconPicker.availableIcons[0].icon,
                                    contentDescription = null,
                                    modifier = Modifier.size(24.dp),
                                    tint = MaterialTheme.colorScheme.outline
                                )
                                Text(
                                    text = "Seleccionar ícono",
                                    style = MaterialTheme.typography.bodyMedium,
                                    color = MaterialTheme.colorScheme.outline
                                )
                            }
                        }
                    }

                    // Grid de iconos (mostrar solo si showIconPicker es true)
                    if (showIconPicker) {
                        Spacer(modifier = Modifier.height(8.dp))

                        LazyVerticalGrid(
                            columns = GridCells.Fixed(4),
                            modifier = Modifier
                                .fillMaxWidth()
                                .height(200.dp),
                            verticalArrangement = Arrangement.spacedBy(8.dp),
                            horizontalArrangement = Arrangement.spacedBy(8.dp)
                        ) {
                            items(IconPicker.availableIcons) { iconOption ->
                                IconSelectionItem(
                                    iconOption = iconOption,
                                    isSelected = selectedIcon == iconOption,
                                    onIconSelected = {
                                        selectedIcon = iconOption
                                        showIconPicker = false
                                    }
                                )
                            }
                        }
                    }
                }
            },
            confirmButton = {
                Button(
                    onClick = {
                        val iconName = selectedIcon?.name ?: IconPicker.availableIcons[0].name
                        onAccept(nombre, iconName)
                        // Reset state
                        nombre = ""
                        selectedIcon = null
                        showIconPicker = false
                    },
                    enabled = nombre.isNotBlank()
                ) {
                    Text("Crear")
                }
            },
            dismissButton = {
                TextButton(onClick = {
                    onDismiss()
                    // Reset state
                    nombre = ""
                    selectedIcon = null
                    showIconPicker = false
                }) {
                    Text("Cancelar")
                }
            }
        )
    }
}

@Composable
fun IconSelectionItem(
    iconOption: IconPicker.IconOption,
    isSelected: Boolean,
    onIconSelected: () -> Unit
) {
    val backgroundColor = if (isSelected) {
        MaterialTheme.colorScheme.primaryContainer
    } else {
        MaterialTheme.colorScheme.surface
    }

    val borderColor = if (isSelected) {
        MaterialTheme.colorScheme.primary
    } else {
        MaterialTheme.colorScheme.outline.copy(alpha = 0.5f)
    }

    Box(
        modifier = Modifier
            .size(48.dp)
            .clip(RoundedCornerShape(8.dp))
            .background(backgroundColor)
            .border(
                width = if (isSelected) 2.dp else 1.dp,
                color = borderColor,
                shape = RoundedCornerShape(8.dp)
            )
            .clickable { onIconSelected() },
        contentAlignment = Alignment.Center
    ) {
        Icon(
            imageVector = iconOption.icon,
            contentDescription = iconOption.name,
            modifier = Modifier.size(24.dp),
            tint = if (isSelected) {
                MaterialTheme.colorScheme.primary
            } else {
                MaterialTheme.colorScheme.onSurface
            }
        )
    }
}

